local E = unpack(ElvUI) -- Import Functions/Constants, Config, Localesif E.db["euiscript"].errorenable ~= true then return endlocal white_list = {	[ERR_INV_FULL] = true,	[ERR_QUEST_LOG_FULL] = true,	[ERR_ITEM_MAX_COUNT] = true,	[ERR_NOT_ENOUGH_MONEY] = true,	[SPELL_FAILED_IN_COMBAT_RES_LIMIT_REACHED] = true,	[ERR_LOOT_MASTER_INV_FULL] = true,	[ERR_LOOT_MASTER_OTHER] = true,	[ERR_LOOT_MASTER_UNIQUE_ITEM] = true,}local frame = CreateFrame("Frame")frame:RegisterEvent("UI_ERROR_MESSAGE")frame:SetScript("OnEvent", function(self, event, text)	if white_list[text] then		UIErrorsFrame:AddMessage(text, 1, 0 ,0)	endend)UIErrorsFrame:UnregisterEvent("UI_ERROR_MESSAGE")